import { ColorName, RuntimePlatform } from './runtime';
/** RuntimePlatform implementing screen manipulation using ANSI escape codes.
 *
 * Ideally this functionality could be made into a mixin, but 1) it relies on this.print() etc and
 * so must be a class and 2) abstract classes don't support mixins yet:
 * https://github.com/microsoft/TypeScript/issues/35356
 */
declare abstract class AnsiTerminalPlatform extends RuntimePlatform {
    moveCursorTo(x: number, y: number): Promise<void>;
    setCursorVisibility(isCursorVisible: boolean): Promise<void>;
    clearScreen(): Promise<void>;
    setFgColor(colorName: ColorName): Promise<void>;
    setBgColor(colorName: ColorName): Promise<void>;
    beep(): Promise<void>;
    /** Translates an ANSI / VT100 key code to the DOS equivalent. */
    translateKeyCode(c: string): string;
}
export default AnsiTerminalPlatform;
